client_script:
frappe.web_form.after_load = () => {
		// Auto populate employee field if user is linked to an employee
		frappe.call({
			method: "frappe.client.get_value",
			args: {
			"doctype": "Employee",
			"filters": {"user_id": frappe.session.user},
			"fieldname": ["name", "employee_name"]
			},
			callback: function(r) {
			if (!r.exc && r.message) {
				frappe.web_form.set_value("employee", frappe.session.user);
				frappe.web_form.set_value("employee_name", r.message.employee_name);
			}
			}
		});
	};
